		asciiTexture = new ASCIITexture(),
		cellSize = 16,
		color = null,
		inverted = false
	} = {}) {

		super("ASCIIEffect", fragmentShader, {
			uniforms: new Map([
				["asciiTexture", new Uniform(null)],
				["cellCount", new Uniform(new Vector4())],
				["color", new Uniform(new Color())]
			])
		});

